import React from 'react';
import Button from '@material-ui/core/Button';
import DeleteIcon from '@material-ui/icons/Delete';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import SaveIcon from '@material-ui/icons/Save';
import Form, {Item, RequiredRule } from 'devextreme-react/form';
import {makeStyles} from '@material-ui/styles';
import IconButton from '@material-ui/core/IconButton';


export default function ShiftDialog(props) {

    const formData ={ ...props.shiftPeriod};
    const useStyles = makeStyles(theme => ({
        button: {
            margin: theme.spacing(1),
            float:'right'
        },
        deleteButton: {
            float: 'left',
            margin: theme.spacing(1),
        },
        dialogAction: {
            display: 'block'
        }
    }))

    const handleClose = () => {
        props.setClose(false);
    };

    const classes = useStyles();

        return (
            <Dialog open={props.open} onClose={handleClose} aria-labelledby="form-dialog-title">
                <DialogTitle id="form-dialog-title">Shift Period</DialogTitle>
                <DialogContent>
                <DialogContentText>
                    Customize your working hours to suit your business.
                </DialogContentText>
                            <Form
                            id={'form'}                
                            formData={formData}
                            readOnly={false}
                            showColonAfterLabel={true}
                            labelLocation={'top'}
                            colCount={2}
                            showValidationSummary={true}
                            validationGroup={'businessOperatingHours'}
                            stylingMode= {'outlined'}
                            >
                                {props.shiftDayOfWeek.map(shift => (  <div>
                                    <Item dataField={'shiftStart'} editorType={'dxDateBox'} editorOptions={{ type: 'time', pickerType: 'rollers', defaultValue:(new Date()).setHours(15,0,0)}} >
                                        <RequiredRule message={'Please select a starting time for your shift'} />
                                    </Item>
                                    <Item dataField={'shiftEnd'} editorType={'dxDateBox'} editorOptions={{ type: 'time', pickerType: 'rollers', defaultValue:'13:00'}}>
                                        <RequiredRule message={'Please select an ending time for your shift'}  />
                                    </Item>  
                                </div>                              
                                 ))}        
                        </Form>
                </DialogContent>
                <DialogActions className={classes.dialogAction} disableSpacing={true}>
                <IconButton aria-label="delete" onClick={() => props.handleDeleteShift()} className={classes.margin}>
                                <DeleteIcon />
                </IconButton>
                <Button variant="contained" color="primary" onClick={() => props.handleSaveShift()} className={classes.button} startIcon={<SaveIcon />}>
                    Save
                </Button>
                </DialogActions>
            </Dialog>
        );
}
